import React, {useState} from "react"
import "./Sign_Login.css"
import axios from "axios"
import { useHistory } from "react-router-dom"
import { toast } from 'react-toastify';
import {ToastContainer} from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
const ValidatedLoginForm = ({ setLoginUser,tcolor,wish,setTcolor,setWish,isWrong,setIsWrong}) => {

    const history = useHistory()

    const [emails,setEmails] = useState();
    const [pass, setPass] = useState();

    const [ user, setUser] = useState({
        email:"",
        password:""
    })

    const handleChange = e => {
        const { name, value } = e.target
        setUser({
            ...user,
            [name]: value
        })

        if(e.target.name === "email"){
            if(!user.email.includes("@")){
              setEmails("*Invalid Email.. must contain @");
            } else{
                setEmails("");
            }
        } 

        if(e.target.name === "password"){
            if(user.password.length<5){
                setPass("*Password must contain atleast 5 length");
            } else{
                setPass("");
            }
        }
    }



    const login = () => {

        axios.post("http://localhost:9002/login", user)
        .then(res => {

             if(res.data.message === "Login Successfull"){
                  console.log(`${res.data.message}`);
                  toast.success(res.data.message);
                  setLoginUser(res.data.user)
                  history.push("/");
                } else if(res.data.message === "Password didn't match"){
                    toast.error("Password did not match");
                } else{
                    toast.warn("Fill the input fields"); 

                }
        })

        if(!tcolor ){
            setWish("red");
            setTcolor(true);
            }
            else{
                setWish("wheat");
                setTcolor(false);
            }
            setIsWrong(1);
    }

    return (
        <div className="login">
           {/* <h1>Login</h1>
            <span className="email">Email</span>
           <input type="text" name="email" value={user.email} onChange={handleChange} placeholder="Enter your Email" pattern="[^ @]*@[^ @]*"   required></input>*/}
       {/* <p className="emailValid">{emails}</p>
          <span className="password">Password</span>
          <p className="passValid">{pass}</p>
          <div className="button" type="submit" onClick={login}>Login</div>
           <div>or</div>
          <div className="button" onClick={() => history.push("/signup")}>signup</div> */}

        </div>
    )
}

export default ValidatedLoginForm;
