import React, {useState} from "react"
import "./Sign_Login.css"
import axios from "axios"
import { useHistory } from "react-router-dom"

const Login = ({ setLoginUser,tcolor,wish,setTcolor,setWish,truth,setTruth,isRight,setIsRight,add,setAdd,quantity,setQuantity}) => {

    const history = useHistory()

    const [emails,setEmails] = useState();
    const [pass, setPass] = useState();

    const [ user, setUser] = useState({
        email:"",
        password:""
    })

    const handleChange = e => {
        const { name, value } = e.target
        setUser({
            ...user,
            [name]: value
        })
    }



    const login = () => {

        if(!truth){
            setAdd(quantity );
            setTruth(true);
            setIsRight(true);
         }
         else{
         setAdd("0");
         setTruth(false);
         setIsRight(false);
         }

        axios.post("http://localhost:9002/login", user)
        .then(res => {
            alert(res.data.message)
            setLoginUser(res.data.user)
            history.push("/");
        })
    }

    return (
        <div className="login">
            <h1>Login</h1>

            <input type="text" name="email" value={user.email} onChange={handleChange} placeholder="Enter your Email" pattern="[^ @]*@[^ @]*" required></input>
            <p>{emails}</p>
            <input type="password" name="password" value={user.password} onChange={handleChange}  placeholder="Enter your Password" ></input>
            <div className="button" type="submit" onClick={login}>Login</div>
            <div>or</div>
            <div className="button" onClick={() => history.push("/signup")}>signup</div>

        </div>
    )
}

export default Login;
