import React, { useState } from "react"
import "./Sign_Login.css"
import axios from "axios"
import { useHistory } from "react-router-dom"
import { toast } from 'react-toastify';
import {ToastContainer} from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
const Signup = ({nam,setNam,final,setFinal}) => {

    const history = useHistory();
    const [emails,setEmails] = useState();
    const [pass, setPass] = useState();
    const [repass, setRePass] = useState();

    const [register1,setRegister] = useState();

    //for enabling the form 

    const [disabled,setDisabled] = useState(false);
    const [man, setMan] = useState(0);
    const [mail, setMail] = useState(0);
    const [pas, setPas] = useState(0);
    const [repas, setRePas] = useState(0); 
    const [fin,setFin] = useState(0);

    const [ user, setUser] = useState({
        name: "",
        email:"",
        password:"",
        reEnterPassword: ""
    })

    const handleChange = e => {
        const { name, value } = e.target
        setUser({
            ...user,
            [name]: value
        })

     if(e.target.name === "name"){
         setFinal(e.target.value);
         setMan(1);
     }
     if(e.target.name === "email"){

        setMail(1);
    }

    if(e.target.name === "password"){
        setPas(1);
    }
    if(e.target.name === "reEnterPassword"){
       setRePas(1);
    }

    // if(e.target.name === "check"){
    //     setFin(1);
    // }

        if(e.target.name === "email"){
            if(!user.email.includes("@")){
              setEmails("*Invalid Email.. must contain @");
            } else{
                setEmails("");
            }
        } 

        if(e.target.name === "password"){
            if(user.password.length<5){
                setPass("*Password must contain atleast 5 length");
            } else{
                setPass("");
            }
        }

        if(e.target.name === "reEnterPassword"){
            if(user.reEnterPassword === user.password){
                setRePass("*RePassword must be same as password")
            } else{
                setRePass("");
            }
        }

    }


    const handleClick = (e) =>{
        setFin(1);
    }

    const register = (e) => {
        const { name, email, password, reEnterPassword } = user

        if( name && email && password && (password === reEnterPassword)){

            setRegister("Success");
            axios.post("http://localhost:9002/register", user)
            .then( res => {

                // alert(res.data.message)
                if(res.data.message === "User already registerd"){
                    toast.error(res.data.message);
                }
                else{
                toast.success(res.data.message);
                history.push("/login")
                }
            })
        } else {
            // alert("invlid input")
            toast.success("invalid input");
            setRegister("*Fill the Input Field First");

        }

    }

    return (
        <div className="register">
            {
            console.log("User", user)

            }



            <h1>Register</h1>
            <span>Name</span>
            <input type="text" name="name" value={user.name} placeholder="Enter Name*" onChange={ handleChange }></input>
            <span>Email</span>
            <input type="text" name="email" value={user.email} placeholder="Enter Email*" onChange={ handleChange } required></input>
            <p className="emailValid">{emails}</p>
            <span>Password</span>
            <input type="password" name="password" value={user.password} placeholder="Enter Password*" onChange={ handleChange }></input>
            <p className="passValid">{pass}</p>
            <span>RePassword</span>
            <input type="password" name="reEnterPassword" value={user.reEnterPassword} placeholder="Enter RePassword*" onChange={ handleChange }></input>
            <p className="repassValid">{repass}</p>

            {/* <input type="checkbox"  className="check"></input>
            <p className="remember">Remember me</p>
             */}

            <input type="checkbox" name="check" value={user.check} className="check" onChange={handleClick}></input>
             <label for="vehicle1" className="remember">Remember me</label>


             { (man && mail && pas && repas && fin) ?  <>
               <div disabled={disabled} className="button" onClick={register}>Register</div>
               <p className="repassValid">{register1}</p>
                <div>or</div>
               <div disabled={disabled} className="button" onClick={() => history.push("/login")}>Login</div> 

               </>:  <>
               <button enabled="true" className="button" onClick={register}>Register</button>
               <p className="repassValid">{register1}</p>
                <div>or</div>
               <button enabled="true" className="button" onClick={() => history.push("/login")}>Login</button> 
               </>
             }      

         </div>


    )
}

export default Signup;
